//8) Program to Find ASCII Value of a Character

package zoho.java.task2;

public class Program8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c1 = 'D', c2 = 'j', c3 = '7';
		System.out.println("ASCII value of " + c1 + " = " +(int)c1);
		System.out.println("ASCII value of " + c2 + " = " +(int)c2);
		System.out.println("ASCII value of " + c3 + " = " +(int)c3);

	}

}
